import Cookies from 'js-cookie'
import jwtDecode from 'jwt-decode';

function isAuthenticated() {
    const token = Cookies.get('token')
    return !!token 
}

export  { isAuthenticated };

export function decodeToken(token) {
    return jwtDecode(token);
}
