import axios from "axios";

let Service = axios.create({
    baseURL: 'http://localhost:10000/',
    timeout: 1000,
});

let Auth = {
    async login(email, password) {
        let response = await Service.post("/auth", {
            email: email,
            password: password,
        })

        let user = response.data

        localStorage.setItem('user', user);
    }
};

export {Service, Auth};
